public class TestSeconds extends TestCase {
    private static final DateTimeZone PARIS = DateTimeZone.forID("Europe/Paris");

    public void testDividedBy() {
        Seconds seconds = Seconds.seconds(10);
        assertEquals(Seconds.seconds(10), seconds.dividedBy(1));
        assertEquals(Seconds.seconds(5), seconds.dividedBy(2));
        assertEquals(Seconds.seconds(2), seconds.dividedBy(5));
        assertEquals(Seconds.seconds(1), seconds.dividedBy(10));
    }

    public void testDividedByZero() {
        Seconds seconds = Seconds.seconds(10);
        try {
            seconds.dividedBy(0);
            fail("Expected ArithmeticException");
        } catch (ArithmeticException e) {
            // expected
        }
    }

    public void testDividedByNegative() {
        Seconds seconds = Seconds.seconds(10);
        try {
            seconds.dividedBy(-1);
            fail("Expected ArithmeticException");
        } catch (ArithmeticException e) {
            // expected
        }
    }
}
