public class TestFixedDateTimeZone extends TestCase {
    private DateTimeZone originalDateTimeZone = null;

    public void testToTimeZone() {
        // Test with a standard offset
        DateTimeZone fixedDateTimeZone = new FixedDateTimeZone("+01:00", "Test", 0, 0);
        TimeZone timeZone = fixedDateTimeZone.toTimeZone();
        assertEquals("Time zone ID", "GMT+01:00", timeZone.getID());
        assertEquals("Time zone offset", 3600000, timeZone.getRawOffset());

        // Test with a non-standard offset
        fixedDateTimeZone = new FixedDateTimeZone("+01:00", "Test", 0, 1);
        timeZone = fixedDateTimeZone.toTimeZone();
        assertEquals("Time zone ID", "Test", timeZone.getID());
        assertEquals("Time zone offset", 3600000, timeZone.getRawOffset());

        // Test with a negative offset
        fixedDateTimeZone = new FixedDateTimeZone("-01:00", "Test", 0, 0);
        timeZone = fixedDateTimeZone.toTimeZone();
        assertEquals("Time zone ID", "GMT-01:00", timeZone.getID());
        assertEquals("Time zone offset", -3600000, timeZone.getRawOffset());

        // Test with a negative non-standard offset
        fixedDateTimeZone = new FixedDateTimeZone("-01:00", "Test", 0, -1);
        timeZone = fixedDateTimeZone.toTimeZone();
        assertEquals("Time zone ID", "Test", timeZone.getID());
        assertEquals("Time zone offset", -3600000, timeZone.getRawOffset());
    }
}
