public class TestMinutes extends TestCase {
    private static final DateTimeZone PARIS = DateTimeZone.forID("Europe/Paris");

    public void testToStandardSeconds() {
        Minutes test = Minutes.minutes(10);
        assertEquals(600, test.toStandardSeconds().getSeconds());
        assertEquals(10, test.getMinutes());
        assertSame(test, test.toStandardSeconds());

        Minutes halfMax = Minutes.minutes(Integer.MAX_VALUE / 2 + 1);
        try {
            halfMax.toStandardSeconds();
            fail();
        } catch (ArithmeticException ex) {
            // expected
        }
    }
}
