    public void testSubAndCheck() {
        assertEquals(MathUtils.subAndCheck(1, 2), -1);
        assertEquals(MathUtils.subAndCheck(Integer.MAX_VALUE, 1), Integer.MAX_VALUE - 1);
        assertEquals(MathUtils.subAndCheck(Integer.MIN_VALUE, 1), Integer.MIN_VALUE - 1);
        assertEquals(MathUtils.subAndCheck(Integer.MAX_VALUE, Integer.MAX_VALUE), 0);
        assertEquals(MathUtils.subAndCheck(Integer.MIN_VALUE, Integer.MIN_VALUE), 0);
        assertEquals(MathUtils.subAndCheck(Integer.MAX_VALUE, Integer.MIN_VALUE), Integer.MAX_VALUE - Integer.MIN_VALUE);
        assertEquals(MathUtils.subAndCheck(Integer.MIN_VALUE, Integer.MAX_VALUE), Integer.MIN_VALUE - Integer.MAX_VALUE);
        assertEquals(MathUtils.subAndCheck(Integer.MAX_VALUE, Integer.MAX_VALUE - 1), Integer.MAX_VALUE - 1);
        assertEquals(MathUtils.subAndCheck(Integer.MIN_VALUE, Integer.MIN_VALUE - 1), Integer.MIN_VALUE - 1);
        assertEquals(MathUtils.subAndCheck(Integer.MAX_VALUE, Integer.MIN_VALUE + 1), Integer.MAX_VALUE - Integer.MIN_VALUE - 1);
        assertEquals(MathUtils.subAndCheck(Integer.MIN_VALUE, Integer.MAX_VALUE + 1), Integer.MIN_VALUE - Integer.MAX_VALUE - 1);
    }

    public void testSubAndCheckOverflow() {
        try {
            MathUtils.subAndCheck(Integer.MAX_VALUE, Integer.MAX_VALUE);
