public final class MathUtilsTest extends TestCase {

    public void testFactorial() {
        long[] fact = {1, 1, 2, 6, 24, 120, 720, 5040, 40320, 362880, 3628800};
        for (int i = 0; i < 11; i++) {
            assertEquals("factorial(" + i + ")", fact[i], MathUtils.factorial(i));
        }

        for (int n = 1; n < 10; n++) {
            assertEquals(n + "!", factorial(n), MathUtils.factorial(n));
            assertEquals(n + "!", (double) factorial(n), MathUtils.factorialDouble(n), Double.MIN_VALUE);
            assertEquals(n + "!", Math.log((double) factorial(n)), MathUtils.factorialLog(n), 10E-12);
        }

        try {
            long x = MathUtils.factorial(67);
            fail("expecting ArithmeticException");
        } catch (ArithmeticException ex) {
            ;
        }

        double x = MathUtils.factorialDouble(1030);
        assertTrue("expecting infinite factorial", Double.isInfinite(x));
    }

