public class FractionTest extends TestCase {
    private static final int SKIP = 500;

    public static Test suite() {
        return new TestSuite(FractionTest.class);
    }

    public void testSubtract() {
        Fraction a = new Fraction(1, 2);
        Fraction b = new Fraction(2, 3);

        assertFraction(1, 2, a.subtract(a));
        assertFraction(-1, 2, a.subtract(b));
        assertFraction(1, 2, b.subtract(a));
        assertFraction(0, 1, b.subtract(b));

        Fraction f1 = new Fraction(3, 5);
        Fraction f2 = Fraction.ZERO;
        try {
            Fraction f = f1.subtract(f2);
            fail("expecting ArithmeticException");
        } catch (ArithmeticException ex) {}

        f1 = new Fraction(0, 5);
        f2 = new Fraction(2, 7);
        Fraction f = f1.subtract(f2);
        assertSame(Fraction.ZERO, f);

        f1 = new Fraction(2, 7);
        f2 = Fraction.ONE;
        f = f1.subtract(f2);
        assertEquals(2, f.getNumerator());
        assertEquals(7, f.getDenominator());

        f1 = new Fraction(1, Integer.MAX_VALUE);
        f = f1.subtract(f1);
        assertEquals(0, f.getNumerator());
        assertEquals(1, f.getDenominator());

        f1 = new Fraction(Integer.MIN_VALUE, Integer.MAX_VALUE);
        f2 = new Fraction(1, Integer.MAX_VALUE);
        f = f1.subtract(f2);
        assertEquals(Integer.MIN_VALUE, f.getNumerator());
        assertEquals(1, f.getDenominator());

        try {
            f.subtract(null);
            fail("IllegalArgumentException");
        } catch (IllegalArgumentException ex) {}

        try {
            f1 = new Fraction(1, Integer.MAX_VALUE);
            f = f1.subtract(f1.reciprocal());  // should overflow
            fail("expecting ArithmeticException");
