public class StrTokenizerTest extends TestCase {
    private static final String CSV_SIMPLE_FIXTURE = "A,b,c";
    private static final String TSV_SIMPLE_FIXTURE = "A\tb\tc";

    public StrTokenizerTest(String name) {
        super(name);
    }

    public static Test suite() {
        return new TestSuite(StrTokenizerTest.class);
    }

    public static void main(String[] args) {
        TestRunner.run(suite());
    }

    public void testClone() {
        StrTokenizer tokenizer = new StrTokenizer(CSV_SIMPLE_FIXTURE);
        StrTokenizer clonedTokenizer = (StrTokenizer) tokenizer.clone();
        assertNotSame(tokenizer, clonedTokenizer);
        assertEquals(tokenizer.getContent(), clonedTokenizer.getContent());
        assertEquals(tokenizer.getDelimiterMatcher(), clonedTokenizer.getDelimiterMatcher());
        assertEquals(tokenizer.getQuoteMatcher(), clonedTokenizer.getQuoteMatcher());
        assertEquals(tokenizer.getIgnoredMatcher(), clonedTokenizer.getIgnoredMatcher());
        assertEquals(tokenizer.getTrimmerMatcher(), clonedTokenizer.getTrimmerMatcher());
        assertEquals(tokenizer.isEmptyTokenAsNull(), clonedTokenizer.isEmptyTokenAsNull());
        assertEquals(tokenizer.isIgnoreEmptyTokens(), clonedTokenizer.isIgnoreEmptyTokens());
    }

    public void testCloneReset() {
        StrTokenizer tokenizer = new StrTokenizer(CSV_SIMPLE_FIXTURE);
        StrTokenizer clonedTokenizer = (StrTokenizer) tokenizer.cloneReset();
        assertNotSame(tokenizer, clonedTokenizer);
        assertEquals(tokenizer.getContent(), clonedTokenizer.getContent());
        assertEquals(tokenizer.getDelimiterMatcher(), clonedTokenizer.getDelimiterMatcher());
        assertEquals(tokenizer.getQuoteMatcher(), clonedTokenizer.getQuoteMatcher());
        assertEquals(tokenizer.getIgnoredMatcher(), clonedTokenizer.getIgnoredMatcher());
        assertEquals(tokenizer.getTrimmerMatcher(), clonedTokenizer.getTrimmerMatcher());
        assertEquals(tokenizer.isEmptyTokenAsNull(), clonedTokenizer.isEmptyTokenAsNull());
        assertEquals(tokenizer.isIgnoreEmptyTokens(), clonedTokenizer.isIgnoreEmptyTokens());
    }
}
