public class VariableFormatterTest extends TestCase {
    private static final String KEY_ANIMAL = "animal";
    private static final String KEY_TARGET = "target";
    static final String REPLACE_TEMPLATE = "The ${animal} jumps over the ${target}.";
    static final String REPLACE_TEMPLATE_EMPTY_KEYS = "The ${} jumps over the ${}.";
    static final String REPLACE_TEMPLATE_NO_ESCAPE = "The {animal} jumps over the {target}.";
    static final String REPLACE_TEMPLATE_NO_MARKERS = "The animal jumps over the target.";
    static final String REPLACE_TEMPLATE_NO_PREFIX = "The $animal} jumps over the $target}.";
    static final String REPLACE_TEMPLATE_NO_SUFFIX = "The ${animal jumps over the ${target.";
    private VariableFormatter format;
    private Map values;

    public void testReplace_Object() {
        // Test with null source
        assertNull(format.replace(null));

        // Test with empty source
        assertEquals("", format.replace(""));

        // Test with source that does not contain any variables
        assertEquals("The animal jumps over the target.", format.replace("The animal jumps over the target."));

        // Test with source that contains variables
        values = new HashMap();
        values.put(KEY_ANIMAL, "lion");
        values.put(KEY_TARGET, "tree");
        format = new VariableFormatter(values);
        assertEquals("The lion jumps over the tree.", format.replace(REPLACE_TEMPLATE));

        // Test with source that contains variables and empty keys
        values = new HashMap();
        values.put("", "empty");
        values.put(KEY_TARGET, "target");
        format = new VariableFormatter(values);
        assertEquals("The empty jumps over the target.", format.replace(REPLACE_TEMPLATE_EMPTY_KEYS));

        // Test with source that contains variables and no escape character
        values = new HashMap();
        values.put(KEY_ANIMAL, "lion");
        values.put(KEY_TARGET, "target");
        format = new VariableFormatter(values, null, null, ' ');
        assertEquals("The lion jumps over the target.", format.replace(REPLACE_TEMPLATE_NO_ESCAPE));

        // Test with source that contains variables and no markers
        values = new HashMap();
        values.put(KEY_ANIMAL, "lion");
        values.put(KEY_TARGET, "target");
        format = new VariableFormatter(values, null, null, ' ');
        assertEquals("The animal jumps over the target.", format.replace(REPLACE_TEMPLATE_NO_MARKERS));

        // Test with source that contains variables and no prefix
        values = new HashMap();
        values.put(KEY_ANIMAL, "lion");
        values.put(KEY_TARGET, "target");
        format = new VariableFormatter(values, null, null, ' ');
        assertEquals("The lion} jumps over the target.", format.replace(REPLACE_TEMPLATE_NO_PREFIX));

        // Test with source that contains variables and no suffix
        values = new HashMap();
        values.put(KEY_ANIMAL, "lion");
        values.put(KEY_TARGET, "target");
        format = new VariableFormatter(values, null, null, ' ');
        assertEquals("The ${animal jumps over the ${target.", format.replace(REPLACE_TEMPLATE_NO_SUFFIX));
    }
}
