public class FractionTest extends TestCase {
    private static final int SKIP = 500;

    public void testPow() {
        Fraction f = null;

        f = Fraction.getFraction(50, 75);
        f = f.pow(2);
        assertEquals(2500, f.getNumerator());
        assertEquals(375, f.getDenominator());

        f = Fraction.getFraction(4, 3);
        f = f.pow(3);
        assertEquals(64, f.getNumerator());
        assertEquals(27, f.getDenominator());

        f = Fraction.getFraction(-15, 47);
        f = f.pow(2);
        assertEquals(225, f.getNumerator());
        assertEquals(221, f.getDenominator());

        f = Fraction.getFraction(0, 3);
        try {
            f = f.pow(2);
            fail("expecting ArithmeticException");
        } catch (ArithmeticException ex) {}

        // large values
        f = Fraction.getFraction(Integer.MIN_VALUE, 1);
        try {
            f = f.pow(2);
            fail("expecting ArithmeticException");
        } catch (ArithmeticException ex) {}

        f = Fraction.getFraction(Integer.MAX_VALUE, 1);
        f = f.pow(2);
        assertEquals(1, f.getNumerator());
        assertEquals(Integer.MAX_VALUE, f.getDenominator());
    }
}
