public class CharSetTest extends TestCase {

    public void testContains_char() {
        CharSet charSet = new CharSet("a-z");
        assertTrue(charSet.contains('a'));
        assertTrue(charSet.contains('z'));
        assertFalse(charSet.contains('1'));
    }

    public void testContains_char_empty() {
        CharSet charSet = new CharSet("");
        assertFalse(charSet.contains('a'));
        assertFalse(charSet.contains('z'));
        assertFalse(charSet.contains('1'));
    }

    public void testContains_char_null() {
        CharSet charSet = new CharSet((String) null);
        assertFalse(charSet.contains('a'));
        assertFalse(charSet.contains('z'));
        assertFalse(charSet.contains('1'));
    }

    public void testContains_char_null_range() {
        CharSet charSet = new CharSet("a-z");
        charSet.add(null);
        assertFalse(charSet.contains('a'));
        assertFalse(charSet.contains('z'));
        assertFalse(charSet.contains('1'));
    }

    public void testContains_char_empty_range() {
        CharSet charSet = new CharSet("a-z");
        charSet.add("");
        assertFalse(charSet.contains('a'));
        assertFalse(charSet.contains('z'));
        assertFalse(charSet.contains('1'));
    }

    public void testContains_char_invalid_range() {
        CharSet charSet = new CharSet("a-z");
        charSet.add("a-");
        assertFalse(charSet.contains('a'));
        assertFalse(charSet.contains('z'));
        assertFalse(charSet.contains('1'));
    }
}
