    }

    public static void main(String[] args) {
        TestRunner.run(suite());
    }

    public void testReset() {
        StrTokenizer tokenizer = new StrTokenizer(CSV_SIMPLE_FIXTURE);
        assertEquals(3, tokenizer.size());
        tokenizer.reset();
        assertEquals(0, tokenizer.size());
    }

    public void testResetWithInput() {
        StrTokenizer tokenizer = new StrTokenizer(CSV_SIMPLE_FIXTURE);
        assertEquals(3, tokenizer.size());
        tokenizer.reset(TSV_SIMPLE_FIXTURE);
        assertEquals(3, tokenizer.size());
    }

    public void testResetWithCharArray() {
        StrTokenizer tokenizer = new StrTokenizer(CSV_SIMPLE_FIXTURE);
        assertEquals(3, tokenizer.size());
        tokenizer.reset(TSV_SIMPLE_FIXTURE.toCharArray());
        assertEquals(3, tokenizer.size());
    }

    public void testResetWithNullInput() {
        StrTokenizer tokenizer = new StrTokenizer(CSV_SIMPLE_FIXTURE);
        assertEquals(3, tokenizer.size());
        tokenizer.reset(null);
        assertEquals(0, tokenizer.size());
    }

    public void testResetWithNullCharArray() {
        StrTokenizer tokenizer = new StrTokenizer(CSV_SIMPLE_FIXTURE);
        assertEquals(3, tokenizer.size());
        tokenizer.reset((char[]) null);
        assertEquals(0, tokenizer.size());
    }

