        assertEquals("hello", CharSetUtils.squeeze("hello", new String[] {null}));
        assertEquals("hello", CharSetUtils.squeeze("hello", new String[] {"a-e"}));

        assertEquals("hlelo", CharSetUtils.squeeze("hello", new String[] { "el" }));
        assertEquals("hlelo", CharSetUtils.squeeze("hello", new String[] { "x" }));
        assertEquals("hlelo", CharSetUtils.squeeze("hello", new String[] { "e-i" }));
        assertEquals("hlelo", CharSetUtils.squeeze("hello", new String[] { "a-z" }));
        assertEquals("hello", CharSetUtils.squeeze("hello", new String[] { "" }));
    }
}
