public class TimeSeriesTests extends TestCase implements SeriesChangeListener {
    private TimeSeries seriesA;
    private TimeSeries seriesB;
    private TimeSeries seriesC;
    private boolean gotSeriesChangeEvent = false;

    public TimeSeriesTests(String name) {
        super(name);
    }

    public static Test suite() {
        return new TestSuite(TimeSeriesTests.class);
    }

    public void testClone() {
        TimeSeries series = new TimeSeries("Test", Day.class);
        series.add(new TimeSeriesDataItem(new Day(1, 1, 2000), 1.0));
        series.add(new TimeSeriesDataItem(new Day(2, 1, 2000), 2.0));
        series.add(new TimeSeriesDataItem(new Day(3, 1, 2000), 3.0));
        TimeSeries clone = (TimeSeries) series.clone();
        assertTrue(clone.equals(series));
        assertTrue(clone.getClass() == series.getClass());
        assertTrue(clone.getTimePeriodClass() == series.getTimePeriodClass());
        assertTrue(clone.getDomainDescription() == series.getDomainDescription());
        assertTrue(clone.getRangeDescription() == series.getRangeDescription());
        assertTrue(clone.getMaximumItemCount() == series.getMaximumItemCount());
        assertTrue(clone.getMaximumItemAge() == series.getMaximumItemAge());
        assertTrue(clone.getItemCount() == series.getItemCount());
        assertTrue(clone.getItems().equals(series.getItems()));
    }

    public void testCloneIndependence() {
        TimeSeries series = new TimeSeries("Test", Day.class);
        series.add(new TimeSeriesDataItem(new Day(1, 1, 2000), 1.0));
        series.add(new TimeSeriesDataItem(new Day(2, 1, 2000), 2.0));
        series.add(new TimeSeriesDataItem(new Day(3, 1, 2000), 3.0));
        TimeSeries clone = (TimeSeries) series.clone();
        clone.updateValue(new TimeSeriesDataItem(new Day(1, 1, 2000), 99.9));
        assertFalse(clone.equals(series));
        clone.updateValue(new TimeSeriesDataItem(new Day(1, 1, 2000), 1.0));
        assertTrue(clone.equals(series));
    }

    public void testSerialization() {
        TimeSeries series = new TimeSeries("Test", Day.class);
        series.add(new TimeSeriesDataItem(new Day(1, 1, 2000), 1.0));
        series.add(new TimeSeriesDataItem(new Day(2, 1, 2000), 2.0));
        series.add(new TimeSeriesDataItem(new Day(3, 1, 2000), 3.0));
        try {
            ByteArrayOutputStream buffer = new ByteArrayOutputStream();
            ObjectOutput out = new ObjectOutputStream(buffer);
            out.writeObject(series);
            out.close();

            ObjectInput in = new ObjectInputStream(
                new ByteArrayInputStream(buffer.toByteArray()));
            TimeSeries clone = (TimeSeries) in.readObject();
            in.close();
            assertTrue(clone.equals(series));
        }
        catch (Exception e) {
            System.out.println("Failed to serialize object.");
        }
    }
}
