    public void testGetDataItem() {
        TimeSeries series = new TimeSeries("Series", Month.class);
        series.add(new Month(MonthConstants.JANUARY, 2003), 45.0);
        series.add(new Month(MonthConstants.FEBRUARY, 2003), 55.0);
        series.add(new Month(MonthConstants.JUNE, 2003), 35.0);
        series.add(new Month(MonthConstants.NOVEMBER, 2003), 85.0);
        series.add(new Month(MonthConstants.DECEMBER, 2003), 75.0);

        TimeSeriesDataItem item = series.getDataItem(0);
        assertEquals(new Month(1, 2003), item.getPeriod());
        assertEquals(45.0, item.getValue(), 0.001);

        item = series.getDataItem(1);
        assertEquals(new Month(2, 2003), item.getPeriod());
        assertEquals(55.0, item.getValue(), 0.001);

        item = series.getDataItem(2);
        assertEquals(new Month(6, 2003), item.getPeriod());
        assertEquals(35.0, item.getValue(), 0.001);

        item = series.getDataItem(3);
        assertEquals(new Month(11, 2003), item.getPeriod());
        assertEquals(85.0, item.getValue(), 0.001);

        item = series.getDataItem(4);
        assertEquals(new Month(12, 2003), item.getPeriod());
        assertEquals(75.0, item.getValue(), 0.001);

        // check negative index
        boolean pass = false;
        try {
            TimeSeriesDataItem result = series.getDataItem(-1);
        }
        catch (IndexOutOfBoundsException e) {
            pass = true;
        }
        assertTrue(pass);

        // check index greater than size
        pass = false;
        try {
            TimeSeriesDataItem result = series.getDataItem(5);
        }
        catch (IndexOutOfBoundsException e) {
            pass = true;
        }
        assertTrue(pass);
    }
}
