public class MonthTests extends TestCase {

    private Month jan1900;
    private Month feb1900;
    private Month nov9999;
    private Month dec9999;

    public MonthTests(String name) {
        super(name);
    }

    public static Test suite() {
        return new TestSuite(MonthTests.class);
    }

    protected void setUp() {
        jan1900 = new Month(1, 1900);
        feb1900 = new Month(2, 1900);
        nov9999 = new Month(11, 9999);
        dec9999 = new Month(12, 9999);
    }

    public void testParseMonth() {
        Month result = null;
        try {
            result = Month.parseMonth("1900-01");
        } catch (TimePeriodFormatException e) {
            fail("Unexpected exception: " + e.getMessage());
        }
        assertEquals(jan1900, result);

        try {
            result = Month.parseMonth("1900-02");
        } catch (TimePeriodFormatException e) {
            fail("Unexpected exception: " + e.getMessage());
        }
        assertEquals(feb1900, result);

        try {
            result = Month.parseMonth("9999-11");
        } catch (TimePeriodFormatException e) {
            fail("Unexpected exception: " + e.getMessage());
        }
        assertEquals(nov9999, result);

        try {
            result = Month.parseMonth("9999-12");
        } catch (TimePeriodFormatException e) {
            fail("Unexpected exception: " + e.getMessage());
        }
        assertEquals(dec9999, result);

