        series1.add(new Day(1, 1, 2022), 1.0);
        series1.add(new Day(2, 1, 2022), 2.0);
        series1.add(new Day(3, 1, 2022), 3.0);
        collection.addSeries(series1);

        TimePeriodValues series2 = new TimePeriodValues();
        series2.add(new Day(1, 1, 2022), 1.0);
        series2.add(new Day(2, 1, 2022), 2.0);
        series2.add(new Day(3, 1, 2022), 3.0);
        collection.addSeries(series2);

        Range expected = new Range(new Day(1, 1, 2022).getStart().getTime(), new Day(3, 1, 2022).getEnd().getTime());
        assertEquals(expected, collection.getDomainBounds(true));

        collection.setXPosition(TimePeriodAnchor.START);
        expected = new Range(new Day(1, 1, 2022).getStart().getTime(), new Day(3, 1, 2022).getStart().getTime());
        assertEquals(expected, collection.getDomainBounds(true));

        collection.setXPosition(TimePeriodAnchor.MIDDLE);
        expected = new Range(new Day(1, 1, 2022).getStart().getTime() + (new Day(3, 1, 2022).getEnd().getTime() - new Day(1, 1, 2022).getStart().getTime()) / 2, new Day(3, 1, 2022).getStart().getTime() + (new Day(3, 1, 2022).getEnd().getTime() - new Day(1, 1, 2022).getStart().getTime()) / 2);
        assertEquals(expected, collection.getDomainBounds(true));

        collection.setXPosition(TimePeriodAnchor.END);
        expected = new Range(new Day(3, 1, 2022).getEnd().getTime(), new Day(3, 1, 2022).getEnd().getTime());
        assertEquals(expected, collection.getDomainBounds(true));
    }
