    public void testGetValue() {
        DefaultKeyedValues2D dataset = new DefaultKeyedValues2D();
        dataset.addValue(1.1, "R1", "C1");
        dataset.addValue(2.2, "R1", "C2");
        dataset.addValue(3.3, "R2", "C1");
        dataset.addValue(4.4, "R2", "C2");

        assertEquals(1.1, dataset.getValue(0, 0), EPSILON);
        assertEquals(2.2, dataset.getValue(0, 1), EPSILON);
        assertEquals(3.3, dataset.getValue(1, 0), EPSILON);
        assertEquals(4.4, dataset.getValue(1, 1), EPSILON);

        // check negative row index throws exception
        try {
            /* Number n = */ dataset.getValue(-1, 0);
            fail("Expected IndexOutOfBoundsException.");
        }
        catch (IndexOutOfBoundsException e) {
            // this is expected
        }

        // check row index == getRowCount() throws exception
        try {
            /* Number n = */ dataset.getValue(dataset.getRowCount(), 0);
            fail("Expected IndexOutOfBoundsException.");
        }
        catch (IndexOutOfBoundsException e) {
            // this is expected
        }

        // check negative column index throws exception
        try {
            /* Number n = */ dataset.getValue(0, -1);
            fail("Expected IndexOutOfBoundsException.");
        }
        catch (IndexOutOfBoundsException e) {
            // this is expected
        }

        // check column index == getColumnCount() throws exception
        try {
            /* Number n = */ dataset.getValue(0, dataset.getColumnCount());
            fail("Expected IndexOutOfBoundsException.");
        }
        catch (IndexOutOfBoundsException e) {
            // this is expected
        }

        // check null row key throws exception
        try {
            /* Number n = */ dataset.getValue(null, 0);
            fail("Expected UnknownKeyException.");
        }
        catch (UnknownKeyException e) {
            // this is expected
        }

        // check null column key throws exception
        try {
            /* Number n = */ dataset.getValue(0, null);
            fail("Expected UnknownKeyException.");
        }
        catch (UnknownKeyException e) {
            // this is expected
        }

        // check unknown row key throws exception
        try {
            /* Number n = */ dataset.getValue("R3", 0);
            fail("Expected UnknownKeyException.");
        }
        catch (UnknownKeyException e) {
            // this is expected
        }

        // check unknown column key throws exception
        try {
            /* Number n = */ dataset.getValue(0, "C3");
            fail("Expected UnknownKeyException.");
        }
        catch (UnknownKeyException e) {
            // this is expected
        }
    }
