public class SimpleHistogramDatasetTests extends TestCase {
    private static final double EPSILON = 0.0000000001;

    public void testClearObservations() {
        SimpleHistogramDataset dataset = new SimpleHistogramDataset("Test");
        dataset.addBin(new SimpleHistogramBin(1.0, 2.0));
        dataset.addBin(new SimpleHistogramBin(3.0, 4.0));
        dataset.addBin(new SimpleHistogramBin(5.0, 6.0));
        assertEquals(3, dataset.getItemCount(0));
        dataset.clearObservations();
        assertEquals(0, dataset.getItemCount(0));
    }

    public void testSerialization() {
        SimpleHistogramDataset dataset = new SimpleHistogramDataset("Test");
        dataset.addBin(new SimpleHistogramBin(1.0, 2.0));
        dataset.addBin(new SimpleHistogramBin(3.0, 4.0));
        dataset.addBin(new SimpleHistogramBin(5.0, 6.0));
        try {
            ByteArrayOutputStream buffer = new ByteArrayOutputStream();
            ObjectOutput out = new ObjectOutputStream(buffer);
            out.writeObject(dataset);
            out.close();

            ObjectInput in = new ObjectInputStream(
                new ByteArrayInputStream(buffer.toByteArray()));
            SimpleHistogramDataset deserializedDataset = (SimpleHistogramDataset) in.readObject();
            in.close();

            assertEquals(dataset, deserializedDataset);
        } catch (Exception e) {
            fail(e.toString());
        }
    }
}
