public class ValueMarkerTests extends TestCase implements MarkerChangeListener {
    MarkerChangeEvent lastEvent;
    private static final double EPSILON = 0.000000001;

    public void testGetSetValue() {
        ValueMarker m = new ValueMarker(1.0);
        m.addChangeListener(this);
        this.lastEvent = null;
        assertEquals(1.0, m.getValue(), EPSILON);
        m.setValue(0.5);
        assertEquals(0.5, m.getValue(), EPSILON);
        assertEquals(m, this.lastEvent.getMarker());
    }

    public void testEquals() {
        ValueMarker m1 = new ValueMarker(1.0);
        ValueMarker m2 = new ValueMarker(1.0);
        assertTrue(m1.equals(m2));
        m2.setValue(0.5);
        assertFalse(m1.equals(m2));
        m1.setValue(0.5);
        assertTrue(m1.equals(m2));
    }

    public void testHashCode() {
        ValueMarker m1 = new ValueMarker(1.0);
        ValueMarker m2 = new ValueMarker(1.0);
        assertEquals(m1.hashCode(), m2.hashCode());
        m2.setValue(0.5);
        assertNotEquals(m1.hashCode(), m2.hashCode());
        m1.setValue(0.5);
        assertEquals(m1.hashCode(), m2.hashCode());
    }

    public void testClone() {
        ValueMarker m1 = new ValueMarker(1.0);
        ValueMarker m2 = (ValueMarker) m1.clone();
        assertTrue(m1.equals(m2));
        assertNotSame(m1, m2);
        m2.setValue(0.5);
        assertTrue(m1.equals(m2));
    }

    public void testSerialization() {
        ValueMarker m1 = new ValueMarker(1.0);
        ValueMarker m2 = null;
        try {
            ByteArrayOutputStream buffer = new ByteArrayOutputStream();
            ObjectOutput out = new ObjectOutputStream(buffer);
            out.writeObject(m1);
            out.close();

            ObjectInput in = new ObjectInputStream(
                new ByteArrayInputStream(buffer.toByteArray()));
            m2 = (ValueMarker) in.readObject();
            in.close();
        } catch (Exception e) {
            e.printStackTrace();
            fail(e.toString());
        }
        assertTrue(m1.equals(m2));
    }

    public void testMarkerChangeEvent() {
        ValueMarker m = new ValueMarker(1.0);
        m.addChangeListener(this);
        this.lastEvent = null;
        m.setValue(0.5);
        assertNotNull(this.lastEvent);
        assertEquals(m, this.lastEvent.getMarker());
    }

    public void markerChanged(MarkerChangeEvent event) {
        this.lastEvent = event;
    }
}
