public class DefaultKeyedValueDatasetTests extends TestCase {

    public void testClone() {
        DefaultKeyedValueDataset dataset = new DefaultKeyedValueDataset();
        dataset.setKey("Test Dataset");
        dataset.setValue(new Comparable() {
            public int compareTo(Object o) {
                return 0;
            }
        }, new Number() {
            public int intValue() {
                return 42;
            }
        });

        DefaultKeyedValueDataset clone = null;
        try {
            clone = (DefaultKeyedValueDataset) dataset.clone();
            clone.setKey("Clone Dataset");
            clone.setValue(new Comparable() {
                public int compareTo(Object o) {
                    return 0;
                }
            }, new Number() {
                public int intValue() {
                    return 10;
                }
            });
        } catch (CloneNotSupportedException e) {
            assertTrue(false);
        }

        assertEquals("Test Dataset", dataset.getKey());
        assertEquals("Clone Dataset", clone.getKey());
        assertEquals(42, dataset.getValue().intValue());
        assertEquals(10, clone.getValue().intValue());
    }
}
