    public void testEquals() {
        StandardCategoryToolTipGenerator generator1 = new StandardCategoryToolTipGenerator();
        StandardCategoryToolTipGenerator generator2 = new StandardCategoryToolTipGenerator();
        assertTrue(generator1.equals(generator2));
        assertTrue(generator2.equals(generator1));

        generator1 = new StandardCategoryToolTipGenerator("labelFormat", new DecimalFormat("#,##0.00"));
        generator2 = new StandardCategoryToolTipGenerator("labelFormat", new DecimalFormat("#,##0.00"));
        assertTrue(generator1.equals(generator2));
        assertTrue(generator2.equals(generator1));

        generator1 = new StandardCategoryToolTipGenerator("labelFormat", new SimpleDateFormat("yyyy-MM-dd"));
        generator2 = new StandardCategoryToolTipGenerator("labelFormat", new SimpleDateFormat("yyyy-MM-dd"));
        assertTrue(generator1.equals(generator2));
        assertTrue(generator2.equals(generator1));

        generator1 = new StandardCategoryToolTipGenerator("labelFormat", new DecimalFormat("#,##0.00"));
        generator2 = new StandardCategoryToolTipGenerator("labelFormat", new SimpleDateFormat("yyyy-MM-dd"));
        assertFalse(generator1.equals(generator2));
        assertFalse(generator2.equals(generator1));

        generator1 = new StandardCategoryToolTipGenerator("labelFormat", new DecimalFormat("#,##0.00"));
        generator2 = new StandardCategoryToolTipGenerator("labelFormat", new DecimalFormat("#,##0.00"));
        generator2.setLabelFormat("newLabelFormat");
        assertFalse(generator1.equals(generator2));
        assertFalse(generator2.equals(generator1));

        generator1 = new StandardCategoryToolTipGenerator("labelFormat", new DecimalFormat("#,##0.00"));
        generator2 = new StandardCategoryToolTipGenerator("labelFormat", new DecimalFormat("#,##0.00"));
        generator2.setFormatter(new SimpleDateFormat("yyyy-MM-dd"));
        assertFalse(generator1.equals(generator2));
        assertFalse(generator2.equals(generator1));
    }

    public void testSerialization() {
        StandardCategoryToolTipGenerator generator1 = new StandardCategoryToolTipGenerator();
        StandardCategoryToolTipGenerator generator2 = null;

        try {
            ByteArrayOutputStream buffer = new ByteArrayOutputStream();
            ObjectOutput out = new ObjectOutputStream(buffer);
            out.writeObject(generator1);
            out.close();

            ObjectInput in = new ObjectInputStream(
                new ByteArrayInputStream(buffer.toByteArray()));
            generator2 = (StandardCategoryToolTipGenerator) in.readObject();
            in.close();
        } catch (Exception e) {
            e.printStackTrace();
            fail("Serialization failed");
        }
        assertEquals(generator1, generator2);
    }
}
