            // but we can still skip any initial values that fall outside the
            // range...
            int index = 0;
            // skip any items that don't need including...
            while (index < itemCount && dataset.getXValue(series, index) 
                    < xLow) {
                index++;
            }
            return Math.max(0, index - 1);
        }
