public class SerializationUtilsTest extends TestCase  {
  static final String CLASS_NOT_FOUND_MESSAGE = "ClassNotFoundSerializationTest.readObject fake exception";
  protected static final String SERIALIZE_IO_EXCEPTION_MESSAGE = "Anonymous OutputStream I/O exception";
  private String iString;
  private Integer iInteger;
  private HashMap iMap;

  public void testSerializeStreamNull() throws Exception {
    try {
      SerializationUtils.serialize(null, new ByteArrayOutputStream());
    } catch (IllegalArgumentException ex) {
      return;
    }
    fail();
  }

  public void testSerializeStreamBadStream() throws Exception {
    try {
      SerializationUtils.serialize(iMap, new OutputStream() {
        public void write(int b) throws IOException {
          throw new IOException(SERIALIZE_IO_EXCEPTION_MESSAGE);
        }
      });
    } catch (SerializationException ex) {
      return;
    }
    fail();
  }

  public void testSerializeStream() throws Exception {
    ByteArrayOutputStream streamReal = new ByteArrayOutputStream();
    SerializationUtils.serialize(iMap, streamReal);

    ByteArrayInputStream inTest = new ByteArrayInputStream(streamReal.toByteArray());
    ObjectInputStream ois = new ObjectInputStream(inTest);
    Object test = ois.readObject();
    ois.close();

    assertNotNull(test);
    assertTrue(test instanceof HashMap);
    assertTrue(test != iMap);
    HashMap testMap = (HashMap) test;
    assertEquals(iString, testMap.get("FOO"));
    assertTrue(iString != testMap.get("FOO"));
    assertEquals(iInteger, testMap.get("BAR"));
    assertTrue(iInteger != testMap.get("BAR"));
    assertEquals(iMap, testMap);
  }
}
