  protected static List createStackedValueList(
      CategoryDataset dataset, Comparable category, double base, boolean asPercentages) {

    List result = new ArrayList();
    double posBase = base;
    double negBase = base;
    double total = 0.0;
    if (asPercentages) {
      total = DataUtilities.calculateColumnTotal(dataset, dataset.getColumnIndex(category));
    }

    int baseIndex = -1;
    int seriesCount = dataset.getRowCount();
    for (int s = 0; s < seriesCount; s++) {
      Number n = dataset.getValue(dataset.getRowKey(s), category);
      if (n == null) {
        continue;
      }
      double v = n.doubleValue();
      if (asPercentages) {
        v = v / total;
      }
      if (v >= 0.0) {
        if (baseIndex < 0) {
          result.add(new Object[] {null, new Double(base)});
          baseIndex = 0;
        }
        posBase = posBase + v;
        result.add(new Object[] {new Integer(s), new Double(posBase)});
      } else if (v < 0.0) {
        if (baseIndex < 0) {
          result.add(new Object[] {null, new Double(base)});
          baseIndex = 0;
        }
        negBase = negBase + v; // '+' because v is negative
        result.add(0, new Object[] {new Integer(-s), new Double(negBase)});
        baseIndex++;
      }
    }
    return result;
  }

