public class TimeSeriesTests extends TestCase implements SeriesChangeListener  {
    private TimeSeries seriesA;
    private TimeSeries seriesB;
    private TimeSeries seriesC;
    private boolean gotSeriesChangeEvent = false;

    public TimeSeriesTests(String name) {
        super(name);
    }

    public static Test suite() {
        return new TestSuite(TimeSeriesTests.class);
    }

    protected void setUp() throws Exception {
        seriesA = new TimeSeries("Series A", Day.class);
        seriesB = new TimeSeries("Series B", Month.class);
        seriesC = new TimeSeries("Series C", Year.class);
        seriesA.add(new Day(1, 1, 2000), 100.0);
        seriesA.add(new Day(2, 1, 2000), 200.0);
        seriesA.add(new Day(3, 1, 2000), 300.0);
        seriesB.add(new Month(1, 2000), 100.0);
        seriesB.add(new Month(2, 2000), 200.0);
        seriesB.add(new Month(3, 2000), 300.0);
        seriesC.add(new Year(2000), 100.0);
        seriesC.add(new Year(2001), 200.0);
        seriesC.add(new Year(2002), 300.0);
    }

    public void testRemoveAgedItems() {
        seriesA.setMaximumItemAge(2);
        seriesA.removeAgedItems(true);
        assertEquals(2, seriesA.getItemCount());
        assertEquals(new Day(3, 1, 2000), seriesA.getTimePeriod(0));
        assertEquals(new Day(2, 1, 2000), seriesA.getTimePeriod(1));

        seriesB.setMaximumItemAge(2);
        seriesB.removeAgedItems(true);
        assertEquals(2, seriesB.getItemCount());
        assertEquals(new Month(3, 2000), seriesB.getTimePeriod(0));
        assertEquals(new Month(2, 2000), seriesB.getTimePeriod(1));

        seriesC.setMaximumItemAge(2);
        seriesC.removeAgedItems(true);
        assertEquals(2, seriesC.getItemCount());
        assertEquals(new Year(2002), seriesC.getTimePeriod(0));
        assertEquals(new Year(2001), seriesC.getTimePeriod(1));
    }

    public void seriesChanged(SeriesChangeEvent event) {
        this.gotSeriesChangeEvent = true;
    }

    public void testRemoveAgedItemsNotify() {
        seriesA.setMaximumItemAge(2);
        this.gotSeriesChangeEvent = false;
        seriesA.removeAgedItems(true);
        assertTrue(this.gotSeriesChangeEvent);

        seriesA.setMaximumItemAge(2);
        this.gotSeriesChangeEvent = false;
        seriesA.removeAgedItems(false);
        assertFalse(this.gotSeriesChangeEvent);
    }
}
```
