        plot.addAnnotation(annotation2);
        assertEquals(2, plot.getAnnotations().size());
        boolean removed = plot.removeAnnotation(annotation1);
        assertTrue(removed);
        assertEquals(1, plot.getAnnotations().size());
        removed = plot.removeAnnotation(new XYTextAnnotation("NotInPlot", 1.0, 2.0));
        assertFalse(removed);
        assertEquals(1, plot.getAnnotations().size());
        removed = plot.removeAnnotation(annotation2);
        assertTrue(removed);
        assertEquals(0, plot.getAnnotations().size());
    }
