    private void checkPointsToJava2D(RectangleEdge edge, Rectangle2D plotArea) {
        double lowerBound = this.axis.getRange().getLowerBound();
        double upperBound = this.axis.getRange().getUpperBound();
        double length = this.axis.valueToJava2D(upperBound, plotArea, edge) - this.axis.valueToJava2D(lowerBound, plotArea, edge);

        assertEquals(this.axis.java2DToValue(this.axis.valueToJava2D(lowerBound, plotArea, edge), plotArea, edge), lowerBound, EPSILON);
        assertEquals(this.axis.java2DToValue(this.axis.valueToJava2D(upperBound, plotArea, edge), plotArea, edge), upperBound, EPSILON);

        assertEquals(this.axis.java2DToValue(this.axis.valueToJava2D(lowerBound, plotArea, edge) + length / 2, plotArea, edge), (lowerBound + upperBound) / 2, EPSILON);
    }
